  dayOfMonthOrdinalParse: /\d{1,2}-(й|го)/,
  ordinal: function (number, period) {
    switch (period) {
      case 'M':
      case 'd':
      case 'DDD':
      case 'w':
      case 'W':
        return number + '-й';

      case 'D':
        return number + '-го';

      default:
        return number;
